import React, { useEffect, useState } from 'react';
import axios from 'axios';
import PartForm from './components/PartForm';
import PartsTable from './components/PartsTable';
import FilterInput from './components/FilterInput';

function App() {
  const [parts, setParts] = useState([]);
  const [form, setForm] = useState({ name: '', category: '', supplier: '', quantity: 0, price: 0 });
  const [filter, setFilter] = useState('');

  const fetchParts = async () => {
    try {
      const res = await axios.get(`/api/parts${filter ? `?name=${filter}` : ''}`);
      setParts(res.data);
    } catch (err) {
      console.error('Error fetching parts:', err);
    }
  };

  useEffect(() => { fetchParts(); }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/parts', form);
      setForm({ name: '', category: '', supplier: '', quantity: 0, price: 0 });
      fetchParts();
    } catch (err) {
      console.error('Error adding part:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/parts/${id}`);
      fetchParts();
    } catch (err) {
      console.error('Error deleting part:', err);
    }
  };

  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Parts Inventory Manager</h1>
      <FilterInput filter={filter} onChange={(e) => setFilter(e.target.value)} onBlur={fetchParts} />
      <PartForm form={form} onChange={handleChange} onSubmit={handleSubmit} />
      <PartsTable parts={parts} onDelete={handleDelete} />
    </div>
  );
}

export default App;
